@import url(https://fonts.googleapis.com/css?family=Poiret+One);


body, .presentation{
     background-image: url(images/image-from-rawpixel-id-12419376-jpeg.jpg); 
     background-repeat: no-repeat;
     background-size: 100%;
     display: flex;
     flex-direction: column;
     justify-content: space-between;
     height: 100vh;
}

h1{
    margin: 50px;
        font-size: 60px;
        animation: flipper 1s ; 

      } 
    .circle {
      position: absolute;
      top: 250px;
        background: none;
        border: none;
        cursor: pointer;
        font-size: 16px;
        font-weight: bold;
        color: white;
        padding: 10px;
        border-radius: 50%;
        width: 200px;
        height: 200px;
        box-shadow: 4px -40px 60px 5px rgb(26, 117, 206) inset;
        transition: background-color 0.3s, transform 0.3s;
    }

        .circle:hover {
            background-color: rgba(0, 0, 0, 0.2);
            transform: scale(1.05);
        }

        /* Styles pour les contenus cach√©s */
        .hidden {
           display: none;
            padding: 80px 30px;
            border-radius: 40px;
            position: absolute;
            top: 60%;
            left: 50%;
            width: 100%;
            height: 80%;
            transform: translate(-50%, -50%);
            z-index: 1000;
            box-shadow: 4px -40px 60px 5px rgb(26, 117, 206) inset;
            max-width: 80%;



          animation: bounce2 2s ease ;
        }
        @keyframes bounce2 {
          0%, 20%, 50%, 80%, 100% {
            opacity: 0;transform: translateY(-500);}
          40% {opacity: 1; translateY(1000px);}
          60% {opacity: 1;transform: translateY(1500px);}
        }
        p{
            font-family: 'Poiret One', 'Open Sans', 'Helvetica Neue', 'Helvetica', 'Arial', sans-serif;
            font-size: 20px;
            padding: 20px 100px;
        }
        .content-container {
          height: 100%;
            display: flex;
            justify-content: space-around;
            align-items: center;

        }


        .close-button{
            position: absolute;
            top: 10px;
            right: 50px;
            margin: 10px 30px;
            display: flex;
            justify-content: flex-end;
            color: black;
            background-color: white;

        }
        .grow {
            animation: grow 2s ease infinite;
          }
          @keyframes grow {
            from { transform: scale(0); }
            to { transform: scale(1); }
          }

          .fade-in {
            animation: fade-in 2s linear infinite;
          }
          @keyframes fade-in {
            from { opacity: 1; 
            scale: 0%;}
            to { opacity: 1; 
            scale: 1;}
          }

        .bounce-in {
            animation: bounce-in 2s ease 1s ;
          }
          @keyframes bounce-in {
            0% {
              opacity: 0;
              transform: scale(.3);
            }
            50% {
              opacity: 1;
              transform: scale(1.05);
            }
            70% { transform: scale(.9); }
            100% { transform: scale(1); }
          }

          .bounce-in-right {
            animation: bounce-in-right 2s ease 1s;
          }
          @keyframes bounce-in-right {
            0% {
              opacity: 0;
              transform: translateX(2000px);
            }
            60% {
              opacity: 1;
              transform: translateX(-30px);
            }
            80% { transform: translateX(10px); }
            100% { transform: translateX(0);
                transform: translateY(100px);  }
          }

          .bounce-out {
            animation: bounce-out 2s ease 1s;
          }
          @keyframes bounce-out {
            0% { transform: scale(1); }
            25% { transform: scale(.95); }
            50% {
              opacity: 1;
              transform: scale(1.1);
            }
            100% {
              opacity: 0;
              transform: scale(.3);
            } 
          }

          .bounce-out-down {
            animation: bounce-out-down 2s ease 2s;
          }
          @keyframes bounce-out-down {
            0% { transform: translateY(0); }
            20% {
              opacity: 1;
              transform: translateY(-200px);
            }
            100% {
              opacity: 0;
              transform: translateY(200px);
            }
          }
          @keyframes flipper{
            from{
               transform: scale(0.0); 
            }
            to{
               transform: scale(1); 
            }
        }


