Index: 
===================================================================
--- 
+++ 
@@ -0,0 +1,139 @@
+ <style>
+     *{
+  margin: 0;
+  padding: 0;
+  font-family: Arial, Helvetica, sans-serif;
+  color: white;
+} 
+       
+       header { 
+  height: 10vh;
+  display: flex;
+  justify-content: space-between;
+  align-items: center;
+  grid-area: header;
+}
+.logo_img img{
+  border-radius: 50%;
+    width: 50%;
+    padding: 0px;
+    position: relative;
+    top: 3vh;
+    left: 50px;
+    box-shadow: 0px 0.5px 5px black;  
+}
+li, a, a:visited {
+  padding: 5px;
+  text-decoration: none;
+  list-style: none;
+}
+a:hover {
+  color: red;
+}
+
+/**** NAVBAR ****/
+nav {
+  display: flex;
+  padding: 20px;
+}
+nav ul {
+  display: flex;
+  justify-content: space-between;
+}
+#icons {
+  cursor: pointer;
+  display: none;
+}
+          .gallery {
+            --n: 1; /* Nombre de lignes */
+            --m: 5; /* Nombre de colonnes */
+            --g: 20px; /* Espace entre les images */
+            --f: 2; /* Facteur d'agrandissement au survol */
+        height: 73vh;
+           display: grid;
+    grid-template-columns: repeat(var(--m), auto);
+    gap: var(--g);
+    max-width: 1200px; /* Largeur maximale pour éviter que la galerie ne devienne trop large */
+    margin: 5vh auto; /* Marges automatiques à gauche et à droite pour centrer la galerie */
+}
+        
+
+        .card_creation {
+            position: relative;
+            overflow: hidden;
+           
+        }
+
+        /* .creation_img {
+            display: block;
+            position: relative;
+            
+           
+        } */
+
+        .creation_img img {
+            width: 100%;
+            height: 100%;
+            object-fit: cover;
+            transform: scale(1);
+            transition: transform 0.3s ease;
+        }
+
+        .creation_img h3 {
+            position: absolute;
+            height: 4vh;
+            bottom: 0;
+            left: 0;
+            width: 100%;
+            background: rgba(0, 0, 0, 0.7);
+            color: white;
+            padding: 5px;
+            margin: 0;
+            opacity: 0;
+            transform: translateY(-100%);
+            transition: opacity 0.3s ease, transform 0.3s ease;
+            text-align: center;
+            font-size: 1em;
+            padding: 10px;
+        }
+
+        .creation_img:hover img {
+            transform: scale(var(--f));
+        }
+
+        .creation_img:hover h3 {
+            opacity: 1;
+            transform: translateY(0);
+        }
+
+        body {
+            margin: 0;
+            background-image: url(images/dark-blue-light-waves-2i61u57tfa11oriq.jpg);
+
+
+        }
+
+        footer{ 
+    padding : 20px;
+    background:  black ;
+    text-align: center;
+}
+footer img{
+  width: 30px;
+  height: 30px;
+  border-radius: 5px;
+
+  
+}
+        @media (max-width: 768px) {
+    .gallery {
+        --m: 1; /* Réduire le nombre de colonnes pour les écrans plus petits */
+    }
+}
+
+/* Styles pour les écrans de taille moyenne */
+@media (min-width: 769px) and (max-width: 1024px) {
+    .gallery {
+        --m: 2; /* Vous pouvez ajuster le nombre de colonnes en conséquence */
+    }
+}

